class Solution:
    def numberOfSubstrings(self, s: str) -> int:
        n = len(s)
        c = 0
        for i in range(n - 2):
            for j in range(i + 2, n):
                # print(s, s[i: j + 1])
                for k in "abc":
                    if k not in s[i: j + 1]:
                        break
                else:
                    c += 1
        
        return c

